import React, { Component } from 'react';
import {NavigationContainer} from '@react-navigation/native';
import {createStackNavigator} from '@react-navigation/stack';

import {
    Text,
    Image,
    StyleSheet,
    View,
    TextInput,
    TouchableOpacity,
    Platform,
    SafeAreaView
} from 'react-native';

export function LoginMiscua({ navigation }){
        return (
            <View style={styles.generalContainer}>
                <SafeAreaView style={styles.safe}>
                    <View style={styles.headerContainer}>
                        <View>
                            <Image style={styles.logo} source={require('../assets/img/01.png')}/>
                        </View>
                        <View style={styles.tittle}>
                            <Text><Text style={styles.sectionTitle}>COLOMBIA </Text><Text style={styles.sectionBoldTitle}>UNIDA</Text></Text>
                        </View>
                    </View>
                    <View style={styles.loginContainer}>
                        <View style={styles.inputView}>
                            <View style={styles.left}>
                                <Image 
                                style={styles.logoTel}
                                source={require('../assets/img/phone-2058848.png')}
                                />
                            </View>
                            <View style={styles.right}>
                                <TextInput
                                    style={styles.inputText}
                                    placeholder='Teléfono'
                                    placeholderTextColor= 'rgb(162, 162, 162)'
                                    returnKeyType={'next'}
                                    keyboardType={'phone-pad'}
                                />
                            </View>
                        </View>
                        <View style={styles.inputView}>
                            <View style={styles.left}>
                                <Image 
                                style={styles.logoTel}
                                source={require('../assets/img/icon-lock-32.png')}
                                />
                            </View>
                            <View style={styles.right}>
                                <TextInput
                                    style={styles.inputText}
                                    placeholder='Contraseña' 
                                    placeholderTextColor= 'rgb(162, 162, 162)'
                                    secureTextEntry= {true}
                                />
                            </View> 
                        </View>
                        <TouchableOpacity style={styles.loginBtn} onPress={() => navigation.navigate('howto')}>
                            <Text style={styles.loginText}>INGRESA</Text>
                        </TouchableOpacity>
                        <TouchableOpacity>
                            <Text style={styles.forgot}>Olvidé mi contraseña</Text>
                        </TouchableOpacity>
                        <TouchableOpacity style={styles.registerText}>
                            <Text><Text style={styles.registerTextWithout}>No tengo cuenta,  </Text><Text style={styles.registerLink}>REGISTRARME</Text></Text>
                        </TouchableOpacity>
                    </View>

            </SafeAreaView>
            </View>
        )};


const styles = StyleSheet.create({
    safe: {
        flex: 1,
        backgroundColor: 'rgb(45, 45, 68)',
    },
    generalContainer: {
        flex: 1,
        backgroundColor: 'rgb(45, 45, 68)',

    },
    headerContainer: {
        flex: 1,
        justifyContent: 'center', 
        alignItems: 'center', 
        marginTop: 20,
        backgroundColor: 'rgb(45, 45, 68)',
        marginBottom: 15,
    },
    logo: {
        width: 260, 
        height: 160, 
        marginTop: Platform.select({
            ios: 80,
            android: 20
        }),
        padding: 10,
        paddingBottom: 0,
        resizeMode: 'contain'
    },
    tittle: {
        marginTop: 10,
    },

    sectionTitle: {
        fontSize: 18,
        fontWeight: '500',
        color: 'white',
        /*paddingTop: 10,*/
        textAlign: 'center',
    },

    sectionBoldTitle: {
        fontSize: 18,
        fontWeight: 'bold',
        color: 'white',
        paddingTop: 10,
        textAlign: 'center',
    },

    loginContainer: {
        flex: 1,
        height: '50%',
        alignItems: 'center',
      justifyContent: 'center',
      fontFamily: 'Montserrat-Medium',
      marginBottom: '100%',
      backgroundColor: 'rgb(45, 45, 68)',
      marginTop: Platform.select({
          ios: 170,
          android: 100
      })
    },
    inputView:{
        flexDirection: 'row',
        width:"80%",
        borderBottomColor: 'rgb(162, 162, 162)',
        borderBottomWidth: 1,
        marginBottom:10,
        justifyContent: 'flex-start',
        paddingLeft:15,
        alignItems: 'center',
      },
      inputText:{
        height:50,
        color:"white",
        justifyContent: 'center',
      },

      logoTel: {
        width: 30, 
        height: 20,  
        padding: 10,
        paddingBottom: 0,
        resizeMode: 'contain'
      },
      right: {
        paddingLeft: 10,
        alignSelf: 'flex-start',
      },

      forgot:{
        color: 'rgb(162, 162, 162)',
        fontSize:14,
        borderBottomColor: 'rgb(162, 162, 162)',
        borderBottomWidth: 1,
        marginTop: 15,
        width: '80%'
      },

      loginBtn:{
        width:"80%",
        backgroundColor: 'rgb(90, 204, 193)',
        borderRadius: 5,
        height:50,
        alignItems:"center",
        justifyContent:"center",
        marginTop:40,
        marginBottom:10
      },
      loginText: {
        color: 'white'
      },
      registerText: {
          paddingTop: 15,
          fontSize: 13
      },
      registerTextWithout: {
        color: 'rgb(162, 162, 162)'
    },
    registerLink: {
        color: 'white'
    }
  },
);
